mapper.select:
 	select jd_id,jd_pic,jd_name,jd_salary,date_format(jd_emptime,'%Y-%m-%d')jd_emptime,jd_company,jd_school,jd_profession from jobdetails where 1=1
 	
 	
 	and jd_name=#{jd_name} 
 	
 	
 	and jd_id=#{jd_id} 
 	
 	
	 and jd_salary=#{jd_salary} 
 	
 	
	 and jd_emptime=#{jd_emptime} 
 	
 	
	 and jd_profession=#{jd_profession}

mapper.delete:
	delete from jobdetails where jp_id=#{jp_id} 
	
 	
 	and jd_name=#{jd_name} 
 	
 	
	 and jd_emptime=#{jd_emptime} 
 	
 	
	 and jd_profession=#{jd_profession}

mapper.update:
update jobdetails set jd_id=#{jd_id}

,jd_name=#{jd_name}

 
,jd_pic=#{jd_pic}

 
,jd_salary=#{jd_salary}

 
,jd_emptime=#{jd_emptime}


,jd_company=#{jd_company}


,jd_school=#{jd_school}


,jd_profession=#{jd_profession}

 where jd_id=#{jd_id}
